import { SET_ALERT, REMOVE_ALERT, ActionTypes } from "../actions/types";

interface Alert {
    id:string;
    message: string;
    alertType: string;
}

interface Action {
    type: ActionTypes;
    payload: any;
}

const initialState: Alert[] = [];

export default function alertReducer(state :Alert[] = initialState, action: Action){
    const {type, payload}= action;

    switch(type) {
        case ActionTypes.SET_ALERT:
            return [...state, payload]
        case REMOVE_ALERT:
            return state.filter(alert=>alert.id!==payload);
        default:
            return state;
    }
}
