import { configureStore, ThunkAction, Action } from '@reduxjs/toolkit';
import rootReducer from './reducers/rootReducer';

const initialState = {};

export const store = configureStore({
    reducer: rootReducer,
    // If you had initial state to pass or middleware to customize, you could specify them here.
    // For middleware, Redux Toolkit includes redux-thunk by default, so you usually don't need to add it manually.
  });
