import React, {useState, useEffect} from 'react';
import { Helmet } from 'react-helmet';
import axios from 'axios';
import {connect} from 'react-redux';
import { setAlert } from '../actions/alert';
import { Oval } from 'react-loader-spinner';


const Contact = ({setAlert}) => {

  useEffect(() => {
    window.scrollTo(0, 0);
  }, [])


  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  })

  const {name, email, subject, message} = formData;

  const [loading, setLoading] = useState(false);

  const onChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>)=> setFormData({...formData, [e.target.name]: e.target.value});

  const onSubmit = (e: React.FormEvent<HTMLFormElement>)=> {
    e.preventDefault();

    axios.defaults.headers.common['Content-Type'] = 'application/json';
    setLoading(true);

    axios.post('http://localhost:8001/api/contacts', {name, email, subject, message})
    .then(res => {
      setAlert('Message Sent', 'success');
      setLoading(false);
      window.scrollTo(0, 0);
    })
    .catch(err=> {
      setAlert('Error With Sending Message', 'error')
      setLoading(false);
      window.scrollTo(0, 0);
    })
  }


  return (
    <div className='contact'>
      <Helmet>
        <title>Real Estate - Contact</title>
        <meta 
          name='description'
          content='Contact us'
        />
      </Helmet>
      <form action=""></form>
    </div>
  )
}

export default Contact
