import {v4 as uuid} from 'uuid';
import { SET_ALERT, REMOVE_ALERT } from './types';
import { Dispatch } from 'redux';

interface AlertPayload {
    msg: string;
    alertType: 'success' | 'error';
    id: string;
  }

  interface AlertAction {
    type: typeof SET_ALERT | typeof REMOVE_ALERT;
    payload?: AlertPayload | string;
  }

  export const setAlert = (
    msg: string,
    alertType: 'success' | 'error',
    timeout = 5000
  ): AlertAction => {
    const id = uuid();
    const alertAction: AlertAction = {
      type: SET_ALERT,
      payload: { msg, alertType, id },
    };

    setTimeout(() => {
      // Dispatch the REMOVE_ALERT action after the timeout
      // You'll need to handle this action in your reducer
      const removeAlertAction: AlertAction = {
        type: REMOVE_ALERT,
        payload: id,
      };
      // Dispatch the removeAlertAction here or in your component
    }, timeout);

    return alertAction;
  };
