import axios from 'axios';
import { setAlert } from './alert';
import {
    SIGNUP_SUCCESS,
    SIGNUP_FAIL,
    LOGIN_SUCCESS,
    LOGIN_FAIL,
    LOGOUT
} from './types';
import { AppThunk } from '../store';
import { Dispatch } from 'redux';


export const login = (email: string, password:string): AppThunk => async (dispatch) => {
    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    }

    const body = JSON.stringify({email, password});
    try{
        const res = await axios.post('http://localhost:8001/api/token/', body, config)

        dispatch({
            type: LOGIN_SUCCESS,
            payload: res.data
        });

        dispatch(setAlert('Authenticated successfully', 'success'));
    } catch (err){
        dispatch({
            type: LOGIN_FAIL
        });

        dispatch(setAlert('Error Authenticating', 'error'));
    }
}

export const signup = (email: string, name: string, password: string, password2: string): AppThunk => async (dispatch) => {
    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    }
    const body = JSON.stringify({name, email, password, password2});

    try{
        const res = await axios.post('http://localhost:8001/api/accounts/signup', body, config)

        dispatch({
            type: SIGNUP_SUCCESS,
            payload: res.data
        });

        dispatch(login(email, password));
    } catch (err){
        dispatch({
            type: SIGNUP_FAIL
        });

        dispatch(setAlert('Error Authenticating', 'error'));
    }
    
}

export const logout = (): AppThunk => (dispatch) => {
    dispatch(setAlert('logout successful.', 'success'));
    dispatch({type: LOGOUT});
}