import React, {useState, useEffect} from 'react';
import { Helmet } from 'react-helmet';
import axios from 'axios';
import {connect} from 'react-redux';
import { setAlert } from '../actions/alert';
import { Oval } from 'react-loader-spinner';

interface ContactProps {
  setAlert: (msg: string, alertType: "success" | "error", timeout?: number) => void;
}

interface FormData {
  name: string;
  email: string;
  subject: string;
  message: string;
}

const Contact: React.FC<ContactProps> = ({setAlert}) => {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  const [formData, setFormData] = useState<FormData>({
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  const { name, email, subject, message } = formData;

  const [loading, setLoading] = useState(false);

  const onChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    axios.defaults.headers.common['Content-Type'] = 'application/json';
    setLoading(true);

    axios
      .post('http://localhost:8001/api/contacts/', { name, email, subject, message })
      .then((res) => {
        setAlert('Message Sent', 'success');
        setLoading(false);
        window.scrollTo(0, 0);
      })
      .catch((err) => {
        setAlert('Error With Sending Message', 'error');
        setLoading(false);
        window.scrollTo(0, 0);
      });
  };



  return (
    <div className='contact'>
      <Helmet>
        <title>Real Estate - Contact</title>
        <meta 
          name='description'
          content='Contact us'
        />
      </Helmet>
      <form className='contact__form' onSubmit={e=> onSubmit(e)}>
        <label className='contact__form__label' htmlFor="name">Name*</label>
        <input className='contact__form__input' name='name' type="text" placeholder='Full Name' onChange={e=> onChange(e)} value={name} required />
        <label className='contact__form__label' htmlFor="email">Email*</label>
        <input className='contact__form__input' name='email' type="email" placeholder='example@gmail.com' onChange={e=> onChange(e)} value={email} required />
        <label className='contact__form__label' htmlFor="email">Subject*</label>
        <input className='contact__form__input' name='subject' type="subject" placeholder='Buying Home' onChange={e=> onChange(e)} value={subject} required />
        <label className='contact__form__label' htmlFor="email">Message*</label>
        <textarea
            className='contact__form__textarea'
            name="message"
            placeholder='Message'
            onChange={e => onChange(e)}
            value={message}
            required
          />
        { loading ?
          <div className='contact__form__loader'>
            <Oval
              visible={true}
              height="80"
              width="80"
              color="#4fa94d"
              ariaLabel="oval-loading"
              wrapperStyle={{}}
              wrapperClass=""
            />
          </div>
          : <button className='contact__form__button'>Send</button> 
        }
      </form>
    </div>
  )
}

export default connect(null, { setAlert })(Contact);
